import { Fragment, useRef, useState } from 'react'
import styles from '../PlayQuiz/PlayQuiz.module.css'
import Button from '../UI/Button/Button'
import Input from '../UI/Input/Input';
import {useDispatch} from 'react-redux';
import UserActions from '../../store/userslice'

let returnOp=(a,b,opr)=>{
    switch(opr)
    {
        case '+' : 
        return (a+b);
        break;
        case '-' : 
        return (a-b);
        break;
        case '/' : 
        return (a/b);
        break;
        case '*' : 
        return (a*b);
        break; 
        default :
        return 'n.a'
    }
}

let Question = (props) => {

    let no = props.Qno;
    let op1 = props.Question.op1;
    let op2 = props.Question.op2;
    let oprOption = ['+','-','/','x'];
    let opr = oprOption[props.Question.opr-1];
    let ans = returnOp(op1,op2,opr);
    let [ansInp,changeAnsInp] = useState('');
    let isFinal = no==props.total;
    let isCorrect = false;
    let isIncorrect = false;
    let isAnswered = false;
    let dispatchFn = useDispatch();

    let checkAnswe =(data,flag=false)=>{
         //checkAns
        if(data.trim().length==0)
            {
                isAnswered = false;
                isCorrect = false;
                isIncorrect = true;
            }
            else if(data===ans)
            {   isAnswered = true;
                isIncorrect = false;
                isCorrect = true;
            }
            else{
                isAnswered = true;
                isCorrect = false;
                isIncorrect = true;
            }
        if(flag){
            //when next Question is pressed;
            changeAnsInp('');
            dispatchFn(UserActions.changeAns({id:no,isAns:isAnswered}));
            dispatchFn(UserActions.changeCorrect({isA:isAnswered}));
        }
    }
    return (
        <Fragment>
                <div id='quesNo' className={styles.questionNo}>Question Number : {no}</div>
                <div id='question' className={styles.question}>
                <div className={styles.op} id='op1'>{op1}</div>
                <div id='operand' className={styles.op}>{opr}</div>
                <div className={styles.op} id='op2'>{op2}</div>
                <div className={styles.op}>=</div>
                <Input data={{className:styles.opr,id:'op3',value:ansInp,onChange:(e)=>{changeAnsInp(e.target.value)},disabled:false}}></Input>
                </div>
                <div id='correctAns' className={styles.correct}>
                {isCorrect && <p className={styles.correctAns}>Correct Answer !</p>}
                {isIncorrect && <p className={styles.wrongAns}>Wrong Answer, Correct Answer is {ans}</p>}
                </div>
                <div id='btn'>
                <Button data={{id:'checkAns',className:styles.btn}}>Check Answer</Button>
                <Button data={{id:'nextQues',className:styles.btn}}>Next Question</Button>
                {isFinal && <Button data={{id:'finishQues',className:styles.btn}}>Finish Quiz</Button>}
                </div>
        </Fragment>
    )
}

export default Question;
