import Button from '../UI/Button/Button'
import styles from './StartQuiz.module.css'
import Input from '../UI/Input/Input'
import {useDispatch, useSelector} from 'react-redux';
import { useState } from 'react';
import UserActions from '../../store/userslice'

let StartQuiz = (props) =>{ 
    let dispatchFn = useDispatch();
    let id = useSelector((state)=>state.userSlice.length+1);
    console.log(useSelector((state)=>state));
    let [uname,changeUname] = useState('');
    let [ques,changeQues] = useState (10);
    let startGame = () => {
        let newUser = {
            id : id,
            name : uname,
            gameState : props.gameState,
            questions : ques,
        }
        dispatchFn(UserActions.setData({payload:newUser}))
    }
    let changeGameState = () =>{
        dispatchFn(UserActions.changeGameState({id:id-1,gameState:1}));
        props.startQuiz(1);
    }
    return(
            <div className={styles["start-info"]}>
            <div className={`${styles.user} ${styles.feild}`}>
            <label htmlFor='user-name'>User Name : </label>
            <Input data={{type:'text',id:'user-name',name:'user-name',className:styles.inp,value:uname,onChange:(e)=>{changeUname(e.target.value)}}}></Input>
            </div>
            <div className={`${styles.ques} ${styles.feild}`}>
            <label htmlFor='ques'>No of Questions : </label>
            <Input data={{type:'number',id:'ques',name:'ques',className:styles.inp,value:ques,onChange:(e)=>{changeQues(e.target.value)}}}></Input>
            </div>
            <Button data = {{'type':'button','className': styles.btn, onClick:()=>{}}}>Start Quiz</Button>
            </div>
    )
} 

export default StartQuiz;
