import styles from './PlayQuiz.module.css'
import Button from '../UI/Button/Button'
import {useSelector} from 'react-redux';
import { useState } from 'react';

let PlayQuiz = (props) =>{
    let data = useSelector((state)=>state.userInfo[props.currentPlayer-1]);
    let uname = data.uname;
    let total = data.questions;
    let [correct1,changeCorrect] = useState(0);
    let [visited,changeVisited] = useState(0);
    let quesList = data.questionsList;
    let [currentQuestion,changeCurrentQuestion] = useState(0);
    let oprOption = ['+','-','/','*'];

    let result = 3;
    return(
        <div className={styles["game-panel"]}>
            <div id='quesNo' className={styles.questionNo}>Question Number : {currentQuestion+1}</div>
            <div id='question' className={styles.question}>
            <div className={styles.op} id='op1'>{quesList[currentQuestion].op1}</div>
            <div id='operand' className={styles.op}>+</div>
            <div className={styles.op} id='op2'>{quesList[currentQuestion].op2}</div>
            <div className={styles.op}>=</div>
            <div className={styles.opr} id='op3' contentEditable='true'></div>
            </div>
            <div id='correctAns' className={styles.correct}>
            <p className={styles.correctAns}>Correct Answer !</p>
            <p className={styles.wrongAns}>Wrong Answer, Correct Answer is {result}</p>
            </div>
            <div id='btn'>
            <Button data={{id:'checkAns',className:styles.btn,onClick:(e)=>{console.log('checking answer')}}}>Check Answer</Button>
            <Button data={{id:'nextQues',className:styles.btn,onClick:(e)=>{console.log('checking answer')}}}>Next Question</Button>
            </div>
            <div className={styles.uname}>{uname}</div>
            <div id='status' className={styles.status}>
            <div className={styles.questionLeft}>Total : {total}</div>
            <div className={styles.score}>Correct: {correct1}</div>
            <div className={styles.left}>Left: {total - visited}</div>
            </div>
        </div>
    )    
}

export default PlayQuiz;
