import {createSlice} from "@reduxjs/toolkit"


let userSlice = createSlice({
    name : 'UserInfo',
    initialState : [],
    reducers: {
        setData : (state,payload) => {
            console.log(payload);
            state.push({
            id : state.length+1,
            uname : payload.uname,
            questions : payload.questions?payload.questions:10,
            gameState : payload.gameState
            }
            )    
        },
        changeGameState: (state,payload) => {
            state[payload.id].gameState = payload.gameState;
        }
    }
    }
)


let UserActions = userSlice.actions;
export {userSlice};
export default UserActions;
